import{_ as s,c as n,aA as l,G as t,B as e,o as i}from"./chunks/framework.CyHSuKli.js";const p="/assets/FanTools_LoginWindow_0.2.2.DGzYNolS.png",g=JSON.parse('{"title":"快速使用","description":"","frontmatter":{},"headers":[],"relativePath":"fantools/start_use.md","filePath":"fantools/start_use.md","lastUpdated":1737970945000}'),c={name:"fantools/start_use.md"};function h(d,a,_,u,m,b){const o=e("NolebaseGitContributors"),r=e("NolebaseGitChangelog");return i(),n("div",null,[a[0]||(a[0]=l('<h1 id="快速使用" tabindex="-1">快速使用 <a class="header-anchor" href="#快速使用" aria-label="Permalink to &quot;快速使用&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">适用范围</p><p>由于工具箱尚在早期开发阶段，这里的快速使用如无特殊标注，则默认仅针对最新版本哦。</p></div><p><code>0.2.1</code>之后，工具箱现在需要通过正儿八经的安装程序安装在系统中。您可以从 <strong><a href="/about/version.html">这里（本文档中的版本列表页面）</a></strong>、<strong><a href="https://github.com/mangofanfan/FanTools2/releases" target="_blank" rel="noreferrer">这里（GitHub的Release页面）</a></strong> 和 <strong><a href="https://ifanspace.top/2024/08/28/510.html" target="_blank" rel="noreferrer">这里（帆域上的版本更新记录页面）</a></strong> 下载最新版本的安装包。</p><h2 id="登录工具箱" tabindex="-1">登录工具箱 <a class="header-anchor" href="#登录工具箱" aria-label="Permalink to &quot;登录工具箱&quot;">​</a></h2><p><img src="'+p+'" alt="登录窗口" data-zoomable="true"> 工具箱实现了一个临时的账号登录系统，系统仍然需要完善，目前只是临时版本。</p><ul><li><strong>邮箱</strong> - 需要一个电子邮件地址，每个账号都与一个邮箱绑定。目前不需要该邮箱实际可用，甚至不会检查该邮箱是否合法。</li><li><strong>激活码</strong> - 充当密码的角色，但是并非强制性。如果邮箱账号没有设置激活码，则登录时也无需输入。</li></ul><h3 id="注册新账号" tabindex="-1">注册新账号 <a class="header-anchor" href="#注册新账号" aria-label="Permalink to &quot;注册新账号&quot;">​</a></h3><p>使用一个未注册过的邮箱连续登录两次即可注册，以后可以使用该邮箱登录。若邮箱未注册，则第一次登录时会有注册提示。</p><p>注册时输入的激活码将作为该邮箱账号的激活码。激活码一旦设置就无法取消只能更改。</p><p>如果尝试注册时得到登录失败，可能是该邮箱已被使用。目前的系统不会验证邮箱的所属（毕竟只是临时系统），所以随便换个吧……</p><h3 id="登录" tabindex="-1">登录 <a class="header-anchor" href="#登录" aria-label="Permalink to &quot;登录&quot;">​</a></h3><p>注册完毕后应当会自动登录。下次使用工具箱时，使用邮箱和激活码（如无则留空）登录即可。</p><p>目前的用户系统中，可以在工具箱首页更改账号资料，但不支持更改邮箱，不支持撤销已经设置的激活码，不支持删除激活码。</p><p>头像系统使用<a href="https://cravatar.cn" target="_blank" rel="noreferrer">cravatar.cn</a>，玩网站的朋友萌可能会有所听闻这个头像服务，这意味着如果您使用包含QQ号的QQ邮箱，可以默认加载该QQ的头像。</p><h2 id="使用工具" tabindex="-1">使用工具 <a class="header-anchor" href="#使用工具" aria-label="Permalink to &quot;使用工具&quot;">​</a></h2><p>使用工具即可。</p><p>未来会支持工具的导入、导出与本地编辑工具源码，支持开发调试自己的工具。工具系统与工具箱本体已经处于解耦状态，但目前工具还只能跟随工具箱本体一同更新。</p><h2 id="关于-设置" tabindex="-1">关于&amp;设置 <a class="header-anchor" href="#关于-设置" aria-label="Permalink to &quot;关于&amp;设置&quot;">​</a></h2><p>就是关于&amp;设置捏……</p>',19)),t(o),t(r)])}const k=s(c,[["render",h]]);export{g as __pageData,k as default};
